#!/bin/bash

echo
echo "##################################"
echo "######## OPENSVC SSH KEYS ########"
echo "##################################"
echo

[[ -f ~vagrant/opensvc-qa.sh ]] && . ~vagrant/opensvc-qa.sh

SSH_PUB_KEYS="ssh-rsa  root@malabar
ssh-rsa  root@pez
ssh-rsa  opensvc-qabot
ssh-rsa  OPENSVC-CVI
ssh-rsa  OPENSVC-AVN
ssh-rsa  OPENSVC-GMT
ssh-rsa  OPENSVC-SGF
ssh-rsa  ansible@avn
ssh-ed25519 AAAAC3NzaC1lZDI1NTE5AAAAIKTEoNNf0l/W3ItpaOkEjssrNU1KGkGyJ3WCo+JnPm5i OPENSVC-CGN"

function update_key {
  local user="$1"
  local public_key="$2"
  local user_home_dir
  local user_ssh_dir
  local user_ssh_authorized_keys
  [ -z "$user" -o -z "$public_key" ] && echo "invalid params: user=$user, public_key=$public_key" && return 1
  user_home_dir=$(getent passwd $user | awk -F: '{print $6}')
  if [ -z "$user_home_dir" ] ;then
     echo "unable to detect user $user home dir"
     return 1
  fi
  user_ssh_dir=$user_home_dir/.ssh
  user_ssh_authorized_keys=$user_home_dir/.ssh/authorized_keys
  if [ ! -d "$user_ssh_dir" ] ; then
    mkdir echo $user_ssh_dir && chown $user: $user_ssh_dir && chmod og-rwx $user_ssh_dir || return 1
    touch $user_ssh_authorized_keys && chown $user: $user_ssh_authorized_keys && chmod og-rwx $user_ssh_authorized_keys || return 1
  fi
  grep "$public_key" $user_ssh_authorized_keys > /dev/null && return 0
  echo "adding new key for user $user ${public_key}"
  echo "$public_key" >> $user_ssh_authorized_keys
  return 0
}

function update_keys {
  echo "$SSH_PUB_KEYS" | while read ssh_key
  do
    update_key root "$ssh_key" || return 1
    update_key vagrant "$ssh_key" || return 1
  done
}

update_keys || exit 1

[[ ! -f /root/.ssh/authorized_keys.install ]] && {
	cp /root/.ssh/authorized_keys /root/.ssh/authorized_keys.install
}

exit 0

